import styled from "styled-components";

export const ListContainer = styled`
padding: 0;
cursor: pointer;
`
export const Row = styled.li`
display: flex;
align-items: center;
justify-content: space-between;
padding: 8px 0;
font-size: 1rem;
`

export const Text = styled.span`
::first-letter {
    text-transform: capitalize;
}
`
