import React, { useEffect, useState } from 'react';
import axios from '../../axios';
import Form from '../Form/Form';
import { Container } from './stylesTodo';

function Todo() {
    const [input, setInput] = useState('');
    const [priority, setPriority] = useState('Medium');
    const [todos, setTodos] = useState([]);
    const [selectedTask, setSelectedTask] = useState(''); // State for selected task

    const fetchData = async () => {
        try {
            const response = await axios.get('/todos'); // Fetch all tasks
            setTodos(response.data);
        } catch (err) {
            console.error('Error fetching tasks:', err.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const handleTaskSelect = (e) => {
        setSelectedTask(e.target.value); // Update selected task state
    };

    return (
        <Container>
            <h2>Task List Dropdown</h2>
            <div>
                <label htmlFor="task-dropdown">Select a Task:</label>
                <select
                    id="task-dropdown"
                    value={selectedTask}
                    onChange={handleTaskSelect}
                >
                    <option value="" disabled>
                        Choose a task
                    </option>
                    {todos.map((todo) => (
                        <option key={todo._id} value={todo._id}>
                            {todo.text}
                        </option>
                    ))}
                </select>
            </div>
            <p>Selected Task: {selectedTask ? todos.find((t) => t._id === selectedTask)?.text : 'None'}</p>
        </Container>
    );
}

export default Todo;
